}
html p, body p {
  margin: 5px 0 5px 0;
  padding: 0%;
}
html .loadingButton, body .loadingButton {
  margin-top: 1rem;
  text-decoration: none;
  color: white;
  background-color: #00A6FB;
  padding: -0.5rem 1rem 0rem 1rem;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 30px;
}
html .loadingAnimation, body .loadingAnimation {
  width: 20px;
  height: 20px;
  margin: 0%;
  padding: 0%;
}
html .countdown, body .countdown {
  display: flex;
  justify-content: center;
  align-items: center;
}

.pembungkus {
