#storyOuterDiv {
  height: 100vh;
  min-height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  background-color: white;
}
#storyOuterDiv #storyHeader {
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 1rem 1rem 1rem 1rem;
  background-color: white;
}
#storyOuterDiv #storyHeader .logo {
  margin-left: 1rem;
