  font-size: 1rem;
  width: fit-content;
}
#storyOuterDiv #storyHeader .logo h1 {
  margin: 0%;
}
#storyOuterDiv #storyHeader svg {
  margin-right: 1rem;
  width: 50px;
  height: 50px;
}
#storyOuterDiv #storyMainDiv {
  display: flex;
  width: 600px;
  height: 100%;
  justify-content: center;
  align-items: center;
}
#storyOuterDiv #storyMainDiv #leftSlide {
  width: 50px;
  height: 50px;
  background: none;
  border: 1px solid black;
  border-radius: 50%;
  cursor: pointer;
  margin-right: 2rem;
}
#storyOuterDiv #storyMainDiv #story {
  position: relative;
  width: 450px;
  height: 95%;
