  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  border-radius: 30px;
  background-color: rgba(0, 0, 0, 0.707);
}
#storyOuterDiv #storyMainDiv #story #profile {
  background-image: linear-gradient(to bottom, black, rgba(0, 0, 0, 0));
  border-radius: 20px;
  display: flex;
  flex-direction: column;
}
#storyOuterDiv #storyMainDiv #story #profile #progressBar {
  margin: 2rem 0.5rem 1rem 0.5rem;
  padding-top: 8px;
  border: 1px solid black;
  background-color: lightgray;
  border-radius: 10px;
}
#storyOuterDiv #storyMainDiv #story #profile #profileDiv {
  margin: 0rem 0.5rem 0rem 0.5rem;
  display: flex;
  justify-content: flex-start;
  align-items: center;
}
#storyOuterDiv #storyMainDiv #story #profile #profileDiv #profileImage img {
  width: 50px;
  height: 50px;
  object-fit: cover;
  border-radius: 50%;
}
#storyOuterDiv #storyMainDiv #story #profile #profileDiv h1 {
