  display: flex;
  justify-content: space-between;
  align-items: center;
}
#storyOuterDiv #storyMainDiv #story #interaction input {
  margin-bottom: 2rem;
  margin-left: 1rem;
  width: 80%;
  padding: 1rem;
  font-size: 16px;
  background: none;
  color: white;
  border: none;
  border-bottom: 1px solid #e7e7e7;
}
#storyOuterDiv #storyMainDiv #story #interaction input::placeholder {
