sources.style.scss:
$phone : 480px;
$tablet : 1024px;
$mainColor: #072AC8;
$secColor: #00A6FB;
$lightMainColor: white;
$line:rgb(231, 231, 231);
$lightSecColor: rgb(240, 246, 250);
$popUpWidth: 200px;

.body,html::-webkit-scrollbar{
    width: 8px;
}

.body,html::-webkit-scrollbar-track{
    background-color: $lightSecColor;
    border-radius: 100px;
}

.body,html::-webkit-scrollbar-thumb{
    background-color: $mainColor;
    border-radius: 100px;
}

html, body{
    margin: 0;
    padding: 0;
    min-height: 100vh;
    width: 100vw;
    overflow-x: hidden;
    display: flex;
    flex-direction: column;
    font-family: Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    p{
        margin: 5px 0 5px 0;
        padding: 0%;
    }
    .loadingButton{
        margin-top:1rem;
        text-decoration: none;
        color: white;
        background-color: $secColor;
        padding: -0.5rem 1rem 0rem 1rem;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
        height: 30px;
    }
    .loadingAnimation{
        width: 20px;
        height: 20px;
        margin: 0%;
        padding: 0%;
    }
    .countdown{
        display: flex;
        justify-content: center;
        align-items: center;
    }
}
// Header
.pembungkus{
    z-index: 1;
    position: fixed;
    height: 60px;
    width: 100%;
    min-width: 320px;
    border-bottom: 2px solid  $line;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: $lightMainColor;
    .header{
        margin: 0%;
        padding: 4px;
        background-color: $lightMainColor;
        
        display: flex;
        justify-content: space-between;
        align-items: center;
        width: 70%;
        height: 55px;
        .logo{
            font-family: "Style Script", cursive;
            font-size: 1rem;
            width: fit-content;
            
        }
        .searchBar{
            display: flex;
            align-items: center;
            justify-content: center;
            width: fit-content ;
            input{
                background-color: rgb(240, 240, 240);
                border-radius: 8px;
                padding: 0.4rem 1.4rem 0.4rem 1.4rem;
                font-size: 0.8rem;
                border: 2px solid rgb(220, 220, 220)
            }
        }
        .iconDiv{
            display: flex;
            justify-content: space-between;
            align-items: center;
            width: 200px;
            svg{
                height: 2rem;
                color: black;
                object-fit: contain;
            }
            img{
                height: 2rem;
                width: 2rem;
                object-fit: contain;
                border-radius: 50%;
            }
