    .innerDiv{
        display: flex;
        background-color: $lightSecColor;
        width: 70%;
        min-height: 100vh;
        padding-top: 2rem;
        
        .leftHomeDiv{
            padding: 4rem 0rem 0rem 0rem;
            width: 66%;
            display: flex;
            align-items: center;
            flex-direction: column;
            .storyDiv::-webkit-scrollbar{
                display: none;
            }
            .storyDiv{
                overflow-x: scroll;
                overflow-y:hidden;
                background-color: $lightMainColor;
                border:1px solid $line;
                width: 100%;
                height: 4rem;
                display: flex;
                padding: 0.8rem;
                .storyDiv{
                    min-width: 3.5rem;
                    min-height: 3.5rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    border: none;
                    margin: 0rem 0.25rem 0rem 0.25rem;
                    padding: 0%;
                    img{
                        object-fit: cover;
                        border-radius: 50%;
                        height: 3.5rem;
                        width: 3.5rem;
                    }   
                }
                
                

            }
        }

        .rightHomeDiv{
            position: fixed;
            top: 60px;
            left: 62.4%;
            min-height: 100vh;
            width: 22.5%;
            background-color: $lightSecColor;
            padding: 2rem 0rem 2rem 1rem;
            .profileSuggestion{
                margin: 0.8rem 0rem 0.8rem 0rem;
                display: flex;
                justify-content: space-between;
                img{
                    object-fit: contain;
                    height: 4rem;
                    width: 4rem;
                    border-radius: 50%;
                    border:1px solid $line;
                }
                .profileDetail{
                    display: flex;
                    div{
                        margin-left: 1rem;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: flex-start;
                        p{
                            margin: 0%;
                            padding: 0%;
                        }
                        .username{
                            font-weight: bold;
                        }
                        .subname{
                            font-weight: 400;
                            color: darkgray;
                        }
                    }
                }
                
                a{
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    text-decoration: none;
                    color: $secColor;
                    font-weight: bold;
                    font-size: 14px;
                }
            }
            span{
                display: flex;
                justify-content: space-between;
                align-items: center;
                color: darkgray;
                font-weight: bold;
                a{
                    text-decoration: none;
                    color: rgb(32, 32, 32);
                    font-weight: 500;
                }
            }

            .friendSuggestion{
                margin: 0.8rem 0rem 0.8rem 0rem;
                display: flex;
                justify-content: space-between;
                img{
                    object-fit: contain;
                    height: 3rem;
                    width: 3rem;
                    border-radius: 50%;
                    border:1px solid $line;
                }
                .profileDetail{
                    display: flex;
                    div{
                        margin-left: 1rem;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: flex-start;
                        p{
                            margin: 0%;
                            padding: 0%;
                        }
                        .username{
                            font-weight: bold;
                        }
                        .subname{
                            font-size: 13px;
                            font-weight: 400;
                            color: darkgray;
                        }
                    }
                }
                
                a{
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    text-decoration: none;
                    color: $secColor;
                    font-weight: bold;
                    font-size: 14px;
                }
            }
            //Footer for home
            .footer{
                height: 100px;
                display: flex;
                flex-direction: column;
                background-color: $lightSecColor;
                justify-content: center;
                align-items: center;
                .links{
                    display: flex;
                    flex-wrap: wrap;
                    width: 100%;
                    justify-content: space-evenly;
                    a{
                        text-decoration: none;
                        font-size: 11px;
                        color: black;
                        margin: 0 4px 0 4px;
                    }
                }
                span{
                    display: flex;
                    justify-content: space-evenly;
                    align-items: center;
                    width: 100%;
                    font-size: 12px;
                    select{
                        font-size: 12px;
                        background-color: $lightSecColor;
                        border:none;
                    }

                }
            }
        }
        @media screen and (max-width: $tablet) {
            .rightHomeDiv{
                display: none;
            }
            .leftHomeDiv{
                width: 100%;
            }
    
        }
    }
    @media screen and (max-width: $phone) {
        .innerDiv{
            width: 100%;
            background-color: $lightMainColor;
            margin: 0;
            padding: 0;
        }
        .leftHomeDiv{
            width: 100%;
            .postDiv{
                z-index: 0;
                width: 95%;
            }
        }

    }
    
}

.postDiv{
    margin: 1rem 0 1rem 0;
    background-color: $lightMainColor;
    border:1px solid $line;
    width: 100%;
    display: flex;
    padding: 0.8rem;
    flex-direction: column;
    .postProfileDiv{
        padding:0.5rem 0rem 0.5rem 0rem;
        display: flex;
        justify-content: space-between;
        width: 100%;
        a{
            display: flex;
            justify-content: center;
            align-items: center;
            text-decoration: none;
            color: black;
            font-weight: 600;
            span{
                margin-left: 1rem;
            }
            span:hover{
                text-decoration: underline;
            }
            img{
                width: 3rem;
                height: 3rem;
                border-radius: 50%;
            }
            

        }
        button{
            width: 40px;
            background-color: transparent;
            border:none;
            cursor: pointer;
        }

    }
    .pictureDiv{
        width: 100%;
        img{
            object-fit: contain;
            width: 100%;
        }
    }
    .interactDiv{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        margin: 1rem 0 1rem 0;

        .interactInnerDiv{
            display: flex;
            width: 100%;
            justify-content: space-between;
            .leftInnerDiv{
                display: flex;
                width: 145px;
                justify-content: space-between;
            }
            button{
                width: 45px;
                cursor: pointer;
                background-color: transparent;
                border:none;
                svg{
                    color: black;
                }
            }
        }
    }
    #likes{
        margin: 0.1rem 0 0.1rem 0;
        padding: 0%;
        width: 100%;
        font-weight: 600;
    }

    .captionDiv{
        display: flex;
        width: 100%;
        #username{
            font-weight: 600;
        }
        #caption{
            margin-left: 0.4rem;
            display: flex;
        }
        #showMore{
            display: flex;
            color: darkgray;
            font-size: 14px;
            justify-content: center;
            align-items: flex-end;
            cursor: pointer;
        }
    }
    .viewMoreComments{
        display: flex;
        justify-content: flex-start;
        align-items: flex-start;
        padding: 0%;
        margin: 0%;
        text-decoration: none;
        color: darkgray;
        font-weight: 400;
        width: 100%;
        
    }
    .commentSection{
        display: flex;
        flex-direction: column;
        width: 100%;
    }
    #time{
        display: flex;
        width: 100%;
        color: darkgray;
        font-size: 12px;
    }
    .inputCommentSection{
        border-top:1px solid $line;
        padding:12px 0 0 0 ;
        margin:12px 0 12px 0; 
        display: flex;
        width: 100%;
        button{
            width: 45px;
            cursor: pointer;
            background-color: transparent;
            border:none;
        }
        input{
            font-weight: 400;
            width: 100%;
            border:none
        }
        #postButton{
            color: $secColor;
            font-weight: 700;
        }
    }
}

@media screen and (max-width: $phone) {
    .postDiv{
        border: none;
        margin:0%;
        margin-top:1rem;
        .inputCommentSection{
            display: none;
        }
    }

}

.comment{
    display: flex;
    width: 100%;
    justify-content: space-between;
    #usernameComment{
        font-weight: 600;
    }
    #comment{
        margin-left: 0.4rem;
    }
    .commentValue{
        display: flex;
        
    }
    .interactComment{
        button{
            width: 30px;
            cursor: pointer;
            background-color: transparent;
            border:none;
        }
    }
}

.errorText{
    color: red;
    font-size: 14px;
    font-weight: 600;
    text-decoration: underline;
}

// Profile Page

.homeOuterDiv{
    display: flex;
    width: 100%;
    min-height: 100vh;
    flex-direction: row;
    align-items: flex-start;
    justify-content: center;
    background-color: $lightSecColor;    
    .homeInnerDiv{
        display: flex;
        flex-direction: column;
        background-color: $lightSecColor;
        width: 70%;
        min-height: 100vh;
        padding-top: 60px;

        .profileUpperDiv{

            display: flex;
            flex-direction: row;
            justify-content: flex-start;
            align-items: center;
            min-height: 300px;
            max-height: fit-content;
            width: 100%;
            .desktopProfileDiv{
                display: flex;
                flex-direction: row;
                justify-content: flex-start;
                align-items: center;
                min-height: 300px;
                max-height: fit-content;
                width: 100%;
                .profileImageDiv{
                    min-height: 200px;
                    height: 100%;
                    width: 30%;
                    display: flex;
                    justify-content: center;
                    align-items: flex-start;
                    img{
                        object-fit: contain;
                        border-radius: 50%  ;
                        width: 180px;
                        height: 180px;
                    }
                }
                .profileDetailDiv{
                    min-width: 400px;
                    min-height: 300px;
                    height: 100%;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: flex-start;
                    
                    .profileUsernameDiv{
                        display: flex;
                        min-width: 450px;
                        max-width: 100%;
                        align-items: center;
                        height: fit-content;
                        
                        button{
                            overflow: hidden;
                            text-decoration: none;
                            color: white;
                            background-color: $secColor;
                            padding: 0.4rem 1rem 0.4rem 1rem;
                            border-radius: 3px;
                            transition-duration: 0.6s;
                            border: none;
                            font-size: 16px;
                            font-weight: 600;
                            width: 120px;
                            margin: 0rem 1rem 0rem 1rem;
                            &:hover{
                                cursor: pointer;
                            }
                        }
                        .followingButton{
                            background-color: white;
                            border: 2px solid black;
                            color: black;
                        }
                        .editProfileButton{
                            width: 120px;
                            background-color: $lightSecColor;
                            padding: 0.4rem 0.5rem 0.4rem 0.5rem;
                            border: 2px solid black;
                            color: black;
                            a{
                                text-decoration: none;
                                width: 120px;
                                background-color: $lightSecColor;
                                
                                
                                color: black;
                            }
                        }
                        .uploadButton{
                            width: 40px;
                            height: 40px;
                            text-decoration: none;
                            color: black;
                        }
                        h1{
                            margin: 0%;
                            font-weight: 300;
                        }
    
                        #dropDown{
                            background-color: $secColor;
                            color: white;
                            border: none;
                            padding: 0rem 1rem 0rem 1rem;
                            min-height: 30px;
                            border-radius: 5px;
                            max-width: 100px;
                            svg{
                                height: 1.5rem;
                                width: 1.5rem;
                            }
                        }
                        #settings{
                            display: none;
                            background: none;
                            border: none;
                            padding: 0rem 1rem 0rem 1rem;
                            min-height: 30px;
                            svg{
                                height: 1.5rem;
                                width: 1.5rem;
                            }
                        }
                    }

                    .profileStatsDiv{
                        margin: 0.7rem 0rem 0.7rem 0rem;
                        display: flex;
                        width: 330px;
                        justify-content: space-between;
                        align-items: center;
                    }
                    .subProfileDiv{
                        margin: 0rem 0rem 0.7rem 0rem;
                        .profileDescription{
                            max-width: 450px;
                        }
                    }
                    #followedBy{
                        color: darkgray;
                        font-size: 12px;
                        font-weight: 600;
                        b{
                            color: black;
                        }
                    }
                }
            }

            .mobileProfileDiv{
                display: none;
                flex-direction: column;
                justify-content: flex-start;
                align-items: center;
                min-height: 300px;
                max-height: fit-content;
                width: 100%;
                .marginDiv{
                    width: 90%;
                    
                    .mobileUpperDiv{
                        display: flex;
                        min-width: 320px;
                        width: 100%;
                        margin-top:1rem;
                        height: 150px;
                        align-items: center;
                        img{
                            width: 120px;
                            height: 100px;
                            border-radius: 50%;
                            object-fit: contain;
                            margin: 0rem 1rem 1rem 0rem;
                        }
                        .mobileUpperRightDiv{
                            display: flex;
                            flex-direction: column;
                            align-items: center;
                            justify-content: center;
                            height: 100%;
                            span{
                                h1{
                                    font-size: 32px;
                                    margin: 0%;
                                    font-weight: 300;
                                }
                                display: flex;
                                min-width: 280px;
                                margin-bottom: 1rem;
                            }
                            #settingButton{
                                width: 40px;
                                background:none;
                                border: none;
                                cursor: pointer;
                                height: 100%;
                            }
                            #dropDown{
                                background-color: $secColor;
                                color: white;
                                border: none;
                                padding: 0rem 1rem 0rem 1rem;
                                min-height: 30px;
                                border-radius: 5px;
                                svg{
                                    height: 1rem;
                                    width: 1rem;
                                }
                            }
                            #follow{
                                background-color: $secColor;
                                color: white;
                                border: none;
                                font-weight: bold;
                                padding: 0rem 1.5rem 0rem 1.5rem;
                                border-radius: 5px;
                                min-height: 30px;
                                min-width: 150px;
                                margin-right: 1rem;
                            }
                        }
                    }
                    .subProfileDiv{
                        width: 100%;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        
                        b{
                            font-weight: 600;
                        }
    
                        .profileDescription{
                            font-weight: 400;
                            max-width: 320px;                        
                            min-width: 260px;
                        }
                    }
                    #followedBy{
                        display: flex;
                        width: 100%;
                        color: darkgray;
                        font-size: 12px;
                        font-weight: 600;
                        b{
                            color: black;
                        }
                    } 
                }
                
                .profileStatsDiv{
                    min-width: 320px;
                    margin: 1rem 0rem 0rem 0rem;
                    display: flex;
                    width: 100%;
                    justify-content: space-around;
                    align-items: center;
                    border-top: 2px solid $line;
                    div{
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                        p{
                            font-weight: 600;
                        }
                        a{
                            text-decoration: none;
                            color: darkgray;
                            font-weight: 400;
                        }
                    }
                }
            }
            
        }

        .profileBottomDiv{
            width: 100%;
            border-top: 2px solid $line;
            display: flex;
            flex-direction: column;
            align-items: center;
            min-width: 320px;
            .postNavbar{
                display: flex;
                width: 100%;
                justify-content: center;
                align-items: center;
                button{

                    background:none;
                    border:none;
                    padding: 1rem 2rem 0rem 2rem;
                    font-size: 12px;
                    color: darkgray;
                    cursor: pointer;
                    a{
                        z-index: 1;
                    }
                }
            }
            .postDiv{
                height: fit-content;
                width: 100%;
                display: flex;
                flex-wrap: wrap;
                flex-direction: row;
                justify-content: flex-start;
                background: $lightSecColor;
                border: none;
                button{
                    cursor: pointer;
                    margin: 1.5%;
                    background: none;
                    border: none;
                    position: relative;
                    width: 30%;
                }
                button:after{
                    content: "";
                    display: block;
                    padding-bottom: 100%;
                }
                img{
                    z-index: 0;
                    position: absolute;
                    top: 0%;
                    right: 0;
                    bottom: 0%;
                    left: 0%;
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    object-position: center;
                }
            }
        }


    }
    @media screen and (max-width: $tablet) {
        .homeInnerDiv{
            width: 100%;
            margin: 0rem 2rem 0rem 2rem;
            
            .profileUpperDiv{
                .desktopProfileDiv{
                    display: none;
                }
                .mobileProfileDiv{
                    display: flex;
                    span{
                        min-width: 220px;
                        width: 100%;
                        button{
                            overflow: hidden;
                            text-decoration: none;
                            color: white;
                            background-color: $secColor;
                            padding: 0.4rem 1rem 0.4rem 1rem;
                            border-radius: 3px;
                            transition-duration: 0.6s;
                            border: none;
                            font-size: 14px;
                            margin-right: 2rem;
                            font-weight: 600;
                            width: 100px;
                            &:hover{
                                cursor: pointer;
                            }
                        }
                        .uploadButton{
                            width: 40px;
                            height: 40px;
                            text-decoration: none;
                            margin-right: 1rem;
                            color: black;
                        }
                        .followingButton{
                            background-color: white;
                            border: 2px solid black;
                            color: black;
                        }
                        .editProfileButton{
                            width: 120px;
                            background-color: $lightSecColor;
                            padding: 0.4rem 0.5rem 0.4rem 0.5rem;
                            margin-right: 1rem;
                            border: 2px solid black;
                            color: black;
                            a{
                                text-decoration: none;
                                color: black;
                            }
                        }
                    }
                }
            }
           
            
        }

    }
    @media screen and (max-width: $phone) {
        .homeInnerDiv{
            width: 100%;
            margin: 0%;
            min-height: 500px;
            .profileUpperDiv{
                .mobileProfileDiv{
                    .marginDiv{
                        margin-left: 2rem;
                        margin-right: 2rem;
                        .mobileUpperDiv{
                            height: fit-content;
                            img{
                                width: 80px;
                                height: 80px;
                            }
                            .mobileUpperRightDiv{
                                span{
                                    min-width: 220px;
                                    width: 100%;
                                    button{
                                        overflow: hidden;
                                        text-decoration: none;
                                        color: white;
                                        background-color: $secColor;
                                        padding: 0.4rem 1rem 0.4rem 1rem;
                                        border-radius: 3px;
                                        transition-duration: 0.6s;
                                        border: none;
                                        font-size: 14px;
                                        font-weight: 600;
                                        width: 100px;
                                        &:hover{
                                            cursor: pointer;
                                        }
                                    }
                                    .followButton{
                                        margin-right: 1rem;
                                    }
                                    .followingButton{
                                        background-color: white;
                                        border: 2px solid black;
                                        color: black;
                                    }
                                    .uploadButton{
                                        margin: 0rem 1rem 0rem 1rem;
                                    }
                                    .editProfileButton{
                                        width: 120px;
                                        background-color: $lightSecColor;
                                        padding: 0.4rem 0.5rem 0.4rem 0.5rem;
                                        margin-right: 0rem;
                                        border: 2px solid black;
                                        color: black;
                                    }
                                    #dropDown{
                                        width: 50px;
                                    }
                                
                                }
                                h1{
                                    font-size: 24px;
                                }
                                #follow{
                                    min-width: 120px;
                                }
                            }
                        }
                    
                    }
                }
                .subProfileDiv{
                    .profileDescription{
                        font-size: 14px;
                    }
                }
                .desktopProfileDiv{
                    display: none;
                }
                .mobileProfileDiv{
                    display: flex;
                }
            }
            .profileBottomDiv{
                .postDiv{
                    button{
                        margin: 0.3%;
                        width: 32%;
                    }
                }
            }
            
        }

    }
}

//loading
.loading{
    display:flex;
    flex-direction: column;
    min-height: 100vh;
    font-size: large;
    font-weight: bold;
    justify-content: center;
    align-items: center;
    background-color: $lightSecColor;
    a{
        text-decoration: underline;
        color: darkgray;
    }
}
// Upload

#uploadOuterDiv{
    display: flex;
    width: 100%;
    min-height: 100vh;
    align-items: center;
    justify-content: center;
    background-color: $lightSecColor;
    padding-top: 5rem;
    #uploadMainDiv{
        display: flex;
        justify-content: space-around;
        align-items: center;
        background-color: $lightSecColor;
        width: 100%;
        height: 500px;
        .successModal{
            display: flex;
            justify-content: center;
            flex-direction: column;
            svg{
                color: rgb(60, 228, 60);
            }
            p{
            color:rgb(60, 228, 60);
            font-size: 18px;
            font-weight: 500;
            margin: 1rem 0 1rem 0;
            }
            #confirm{
                background-color: rgb(9, 199, 9);
                color: white;
                font-size: 18px;
                font-weight: 700;
                border:none;
                border-radius:10px;
                padding: 0.5rem;
                cursor: pointer;
            }
        }
        #uploadFormDiv{
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            height: fit-content;
            width: 480px;
            background-color: $lightSecColor;
            #uploadForm{
                padding: 2rem 4rem 4rem 4rem;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: space-between;
                background-color: $lightMainColor;
                border: 1px solid $line;
                height: 650px;
                h1{
                    margin: 0%;   
                    padding: 0%;
                    font-weight: 400;
                    width: 100%;
                    display: flex;
                }
                #uploadArea{
                    width: 100%;
                    min-height: 300px;
                    max-height: 300px;
                    border: 2px solid black;
                    border-radius: 10px;
                    position: relative;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    svg{
                        position: absolute;
                    }
                    #upload{
                        width: 100%;
                        height: 100%;
                        position: absolute;
                        opacity: 0;
                        cursor: pointer;
                    }
                    
                }
                #errorLabel{
                    font-size: 15px;
                    font-weight: 500;
                    color: red;
                    width: 100%;
                    text-align: center;
                }
                #imageCarouselDiv{
                    width: 100%;
                    max-height: 300px;
                    min-height: 300px;
                    border: 2px solid black;
                    border-radius: 10px;
                    position: relative;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    #leftSlide{
                        width: 50px;
                        height: 50px;
                        background:none;
                        border: none;
                        cursor: pointer;
                        margin-right: 2rem;
                        position: absolute;
                        left: 0%;
                        z-index: 10;
                    }
                    .uploadImage{
                        width: 100%;
                        height: 100%;
                        object-fit: cover;
                        display: flex;
                        z-index: 5;
                    }
                    #rightSlide{
                        width: 50px;
                        height: 50px;
                        background:none;
                        border: none;
                        cursor: pointer;
                        position: absolute;
                        right: 0;
                        z-index: 10;
                    }
                }
                #textAreaDiv{
