    flex-direction: column;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);
    overflow-y: scroll;
    height: 300px;
    z-index: 10;
    #activityPopUpFooter{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        #showMore{
            font-size: 18px;
            color: $secColor;
            font-weight: 600;
            border-top: 1px solid $line;
            width: 100%;
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 0.4rem;
            padding-top: 1rem;
            &:hover{
                color: $mainColor;
            }
        }
    }
    a{
        font-size: 16px;
        text-decoration: none;
        display: flex;
        justify-content: flex-start;
        align-items: center;
        padding: 0.3rem 0rem 0.3rem 0rem;
        color: black;
        svg{
            width: 24px;
            padding: 0.2rem 0.5rem 0.2rem 0rem;
            object-fit: contain;
        }
        button{
            
            font-size: 16px;
            font-weight: 500;
            padding: 0.5rem 0.5rem 0rem 0.5rem;
            width: 100%;
            background-color: $lightMainColor;
            border: none;
            border-top:2px solid $line;
            cursor: pointer;
            display: flex;
            justify-content: flex-start;
            align-items: center;
        }
        
    }
}

// activity page
#activityOuter{
    height: fit-content;
    min-height: 100vh;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: $lightSecColor;
    padding: 60px 0rem 60px 0rem;
    #activityMain{
        width: 70%;
        display: flex;
        flex-direction: column;
        padding: 1rem 1rem 1rem 1rem;
        background-color: $lightMainColor;
        height: fit-content;
        min-height: 100%;
        margin-top: 5rem;
    }
    @media screen and (max-width: $phone) {
        #activityMain{
            width: 100%;
            margin-top: 0%;
        }
    }
}

.searchPopUp{
    position: fixed;
    width: 200px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: $lightMainColor;
    border-radius: 10px;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);
    height: fit-content;
    min-height: 40px;
    text-decoration: none;
    padding: 0rem 1rem 0rem 1.2rem;
    .loadingButton{
        background:none;
        border: none;
        height: 100%;
        padding: 0%;
        margin: 0%;
    }
    .searchStrip{
        display: flex;
        width: 100%;
        height: 30px;
        margin: 0.3rem 1rem 0.3rem 1rem;
        text-decoration: none;
        align-items: center;
        img{
            height: 30px;
            width: 30px;
            object-fit: cover;
            border-radius: 50%;
            margin-right: 1rem;
        }
        p{
            font-size: 14px;
            width: 100%px;
            margin: 0%;
            padding: 0%;
            color: black;
            overflow: hidden;
            text-overflow: ellipsis;
            font-weight: 600;
        }
        &:hover{
            
        }
    }
}
