  justify-content: space-around;
  align-items: center;
  border-top: 2px solid #e7e7e7;
}
.homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .profileStatsDiv div {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .profileStatsDiv div p {
  font-weight: 600;
}
.homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .profileStatsDiv div a {
  text-decoration: none;
  color: darkgray;
  font-weight: 400;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv {
  width: 100%;
  border-top: 2px solid #e7e7e7;
  display: flex;
  flex-direction: column;
  align-items: center;
  min-width: 320px;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postNavbar {
  display: flex;
  width: 100%;
  justify-content: center;
  align-items: center;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postNavbar button {
  background: none;
  border: none;
  padding: 1rem 2rem 0rem 2rem;
  font-size: 12px;
  color: darkgray;
  cursor: pointer;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postDiv {
  height: fit-content;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: flex-start;
  background: #f0f6fa;
  border: none;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postDiv button {
  cursor: pointer;
  margin: 1.5%;
  background: none;
  border: none;
  position: relative;
  width: 30%;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postDiv button:after {
  content: "";
  display: block;
  padding-bottom: 100%;
}
.homeOuterDiv .homeInnerDiv .profileBottomDiv .postDiv img {
  z-index: 1;
  position: absolute;
  top: 0%;
  right: 0;
  bottom: 0%;
  left: 0%;
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
}
@media screen and (max-width: 1024px) {
  .homeOuterDiv .homeInnerDiv {
    width: 100%;
    margin: 0rem 2rem 0rem 2rem;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .desktopProfileDiv {
    display: none;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv {
    display: flex;
  }
}
@media screen and (max-width: 480px) {
  .homeOuterDiv .homeInnerDiv {
    width: 100%;
    margin: 0%;
    min-height: 500px;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv {
    margin-left: 2rem;
    margin-right: 2rem;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv .mobileUpperDiv {
    height: fit-content;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv .mobileUpperDiv img {
    width: 80px;
    height: 80px;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv .mobileUpperDiv .mobileUpperRightDiv span {
    min-width: 220px;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv .mobileUpperDiv .mobileUpperRightDiv h1 {
    font-size: 24px;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .mobileProfileDiv .marginDiv .mobileUpperDiv .mobileUpperRightDiv #follow {
    min-width: 120px;
  }
  .homeOuterDiv .homeInnerDiv .profileUpperDiv .subProfileDiv .profileDescription {
