sources.style.scss:
$phone : 480px;
$tablet : 1024px;
$mainColor: #072AC8;
$secColor: #00A6FB;
$lightMainColor: white;
$line:rgb(231, 231, 231);
$lightSecColor: rgb(240, 246, 250);
$popUpWidth: 200px;

.body,html::-webkit-scrollbar{
    width: 8px;
}

.body,html::-webkit-scrollbar-track{
    background-color: $lightSecColor;
    border-radius: 100px;
}

.body,html::-webkit-scrollbar-thumb{
    background-color: $mainColor;
    border-radius: 100px;
}

html, body{
    margin: 0;
    padding: 0;
    min-height: 100vh;
    width: 100vw;
    overflow-x: hidden;
    display: flex;
    flex-direction: column;
    font-family: Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    p{
        margin: 5px 0 5px 0;
        padding: 0%;
    }
}
// Header
.pembungkus{
    z-index: 10;
    position: fixed;
    height: 60px;
    width: 100%;
    min-width: 320px;
    border-bottom: 2px solid  $line;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: $lightMainColor;
    .header{
        margin: 0%;
        padding: 4px;
        background-color: $lightMainColor;
        
        display: flex;
        justify-content: space-between;
        align-items: center;
        width: 70%;
        height: 55px;
        .logo{
            font-family: "Style Script", cursive;
            font-size: 1rem;
            width: fit-content;
            
        }
        .searchBar{
            display: flex;
            align-items: center;
            justify-content: center;
            width: fit-content ;
            input{
                background-color: rgb(240, 240, 240);
                border-radius: 8px;
                padding: 0.4rem 1.4rem 0.4rem 1.4rem;
                font-size: 0.8rem;
                border: 2px solid rgb(220, 220, 220)
                
            }
        }
        .iconDiv{
            display: flex;
            justify-content: space-between;
            align-items: center;
            width: 200px;
            svg{
                height: 2rem;
                color: black;
                object-fit: contain;
            }
            img{
                height: 2rem;
                width: 2rem;
                object-fit: contain;
                border-radius: 50%;
            }
            #msgMobile{
                display: none;
            }
        }
        .navDiv{
            display: flex;
            justify-content: space-between;
            align-items: center;
            width: 160px;
            a{
                overflow: hidden;
                text-decoration: none;
                color: white;
                background-color: $secColor;
                padding: 0.7rem;
                border-radius: 7px;
                transition-duration: 0.6s;
                font-family: Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
                font-size: 1rem;
                font-weight: 500;
                &:hover{
                    background-color:$mainColor;
                    color: $lightMainColor;
                }
               
            }
        }
    }
    @media screen and (max-width: $tablet){
        .header{
            margin: 0%;
            padding: 0 1rem 0 1rem;
            width: 100%;
            height: 50px;
            .logo{
                font-size: 1rem;
                width: fit-content;
            }
            .searchBar{
                width: fit-content;
                
                input{
                    padding: 0.4rem 1.4rem 0.4rem 1.4rem;
                    font-size: 0.8rem;
                    width: 130px;
                    margin: 0 1rem 0 1rem;
                }
            }
            .iconDiv{
                width: fit-content;
                svg{
                    height: 2rem;
                    padding: 0 0.2rem 0 0.2rem;
                }
                img{
                    height: 2rem;
                    width: 2rem;
                }
            }
        }
    }
    @media screen and (max-width: $phone) {
        .header{
            width: 100%;
            height: 40px;
            .logo{
                font-size: 1rem;
                width: fit-content;
            }
            .searchBar{
                display: none;
                width: fit-content;
                input{
                    padding: 0.1rem 1.1rem 0.1rem 1.1rem;
                    font-size: 0.8rem;
                    width: 90px;
                }
            }
            .iconDiv{
                width: fit-content;
                svg{
                    height: 2.5rem;
                    padding: 0 0.5rem 0 0.5rem;
                }
                img{
                    height: 2rem;
                    width: 2rem;
                }
                #home{
                    display: none;
                }
                #explore{
                    display:none;
                }
                #profile{
                    display: none;
                }
                #msg{
                    display: none;
                }
                #msgMobile{
                    display:block;
                }
            }
        }
    }
    
}


//Login
#loginOuterDiv{
    display: flex;
    width: 100%;
    min-height: 100vh;
    align-items: center;
    justify-content: center;
    background-color: $lightSecColor;
    #loginMainDiv{
        display: flex;
        justify-content: space-around;
        align-items: center;
        background-color: $lightSecColor;
        width: 700px;
        min-height: 600px;
        height: 600px;
        #loginImageDiv{
            img{
                object-fit: contain;
                height: 650px;
            }
            
        }
        #loginFormDiv{
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            height: 600px;
            background-color: $lightSecColor;
            #loginForm{
                padding: 3rem 2.5rem 3rem 2.5rem;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: space-between;
                background-color: $lightMainColor;
                border: 1px solid $line;
                height: 330px;
                #title{
                    font-family: "Style Script", cursive;
                    font-size: 3rem;
                    width: fit-content;
                    margin: 0;
                }

                form{
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    width: 100%;
                    input{
                        padding: 0.4rem 1rem 0.4rem 1rem;
                        border:1px solid $line;
                        border-radius: 2px;
                        margin-top:0.4rem;
                        width: 200px;
                        font-size: 12px;
                        background-color: rgb(240, 240, 240);
                        color: black;
                    }
                    #loginButton{
                        margin-top:1rem;
                        overflow: hidden;
                        text-decoration: none;
                        color: white;
                        background-color: $secColor;
                        padding: 0.4rem 1rem 0.4rem 1rem;
                        border-radius: 3px;
                        transition-duration: 0.6s;
                        
                        font-size: 0.8rem;
                        font-weight: 600;
                        width: 100%;
                        &:hover{
                            cursor: pointer;
                        }
                    }
                }

                #orDiv{
                    display: flex;
                    width: 100%;
                    justify-content: space-between;
                    color: darkgray;
                    p{
                        margin:0px 8px 0px 8px;
                    }
                    hr{
                        background-color: darkgray;
                        height: 0.1px;
                        width: 100%;
                    }
                }

                #altLogin{
                   display: flex;
                   justify-content: center;
                   align-items: center;
                   background-color: $lightMainColor;
                   border: none;
                   width: 100%;
                    span{
                        color: $mainColor;
                        font-size: 1rem;
                        font-weight: 600;
                    }

                    img{
                        object-fit: contain;
                        height: 20px;
                        margin-right:5px;
                    }

                    &:hover{
                        cursor: pointer;
                    }
                    
                }

                a{
                    text-align: center;
                    width: 100%;
                    text-decoration: none;
                    font-size: 0.8rem;
                }
            }
            
            #signUpDiv{
                padding: 1rem 2.5rem 1rem 2.5rem;
                background-color: $lightMainColor;
                border: 1px solid $line;
                a{
                    text-decoration: none;
                    margin-left: 5px;
                    color: $secColor;
                    font-weight: 500;
                }
            }

            #downloadDiv{
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: center;
                height: 100px;
                p{
                    font-size: 14px;
                    font-weight: 300;
                }
                span{
                    margin-top: 10px;
                    display: flex;
                    justify-content: space-evenly;
                    width: 100%;
                    padding:0 1rem 0 1rem;
                    img{
                        object-fit: contain;
                        height: 45px;
                    }
                    
                }
            }
        }
        
    }
    @media screen and (max-width: $tablet){
        #loginImageDiv{
            display: none;
        }
    }
    @media screen and (max-width: $phone) {
        
        #loginMainDiv{
            min-width: 320px;
            height: 100%;
            background-color: $lightMainColor;
            #loginImageDiv{
                display: none;
            }
            #loginFormDiv{
                width: 100%;
                
                min-width: 260px;
                background: $lightMainColor;
                #loginForm{
                    border:none;
                    form{
                        input{
                            width: 100%;
                            padding: 0.7rem 0rem 0.7rem 0rem;
                        }
                    }
                    
                }
                #signUpDiv{
                    display: flex;
                    justify-content: center;
                    border:none;
                    height: fit-content;
                }
            }
        
        }
    }
}
//Footer
.footer{
    height: 100px;
    display: flex;
    flex-direction: column;
    background-color: $lightSecColor;
    justify-content: center;
    align-items: center;
    .links{
        display: flex;
        flex-wrap: wrap;
        width: 600px;
        justify-content: space-evenly;
        a{
            text-decoration: none;
            font-size: 11px;
            color: black;
            margin: 0 4px 0 4px;
        }
    }
    span{
        display: flex;
        justify-content: space-evenly;
        align-items: center;
        width: 400px;
        font-size: 12px;
        select{
            font-size: 12px;
            background-color: $lightSecColor;
            border:none;
        }

    }
    @media screen and (max-width: $tablet){
        .links{
            width: 500px;
        }
    }
    @media screen and (max-width: $phone) {
        .links{
            width: 300px;
        }
    }
}


//Sign Up
#registerOuterDiv{
    display: flex;
    width: 100%;
    min-height: 100vh;
    align-items: flex-start;
    justify-content: center;
    background-color: $lightSecColor;
    #registerMainDiv{
        display: flex;
        justify-content: space-around;
        align-items: center;
        background-color: $lightSecColor;
        width: 100%;
        height: 100%;
        
        #registerFormDiv{
            display: flex;
            margin-top:4rem;
            flex-direction: column;
            justify-content: space-between;
            height: 820px;
            width: 360px;
            background-color: $lightSecColor;
            .signUp{
                color: darkgray;
                text-align: center;
                font-size: 18px;
                font-weight: 500;
            }
            .terms{
                color: darkgray;
                text-align: center;
                font-size: 12px;
                font-weight: 300;
                a{
                    font-weight: 500;
                    color: darkgray;
                }
            }
            #registerForm{
                padding: 2rem 2.5rem 2rem 2.5rem;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: space-between;
                background-color: $lightMainColor;
                border: 1px solid $line;
                height: 550px;
                
                #title{
                    font-family: "Style Script", cursive;
                    font-size: 3rem;
                    width: fit-content;
