            align-items: center;
            min-height: 300px;
            max-height: fit-content;
            width: 100%;
            .desktopProfileDiv{
                display: flex;
                flex-direction: row;
                justify-content: flex-start;
                align-items: center;
                min-height: 300px;
                max-height: fit-content;
                width: 100%;
                .profileImageDiv{
                    min-height: 200px;
                    height: 100%;
                    width: 30%;
                    display: flex;
                    justify-content: center;
                    align-items: flex-start;
                    img{
                        object-fit: contain;
                        border-radius: 50%  ;
                        width: 180px;
                        height: 180px;
                    }
                }
                .profileDetailDiv{
                    min-width: 400px;
                    min-height: 300px;
                    height: 100%;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: flex-start;
                    
                    .profileUsernameDiv{
                        display: flex;
                        min-width: 450px;
                        max-width: 100%;
                        justify-content: space-between;
                        align-items: center;
                        height: fit-content;
                        h1{
                            margin: 0%;
                            font-weight: 300;
                        }
                        #follow{
                            background-color: $secColor;
                            color: white;
                            border: none;
                            font-weight: bold;
                            padding: 0rem 1.5rem 0rem 1.5rem;
                            border-radius: 5px;
                            min-height: 30px;
                        }
    
                        #dropDown{
                            background-color: $secColor;
                            color: white;
                            border: none;
                            padding: 0rem 1rem 0rem 1rem;
                            min-height: 30px;
                            border-radius: 5px;
                            svg{
                                height: 1.5rem;
                                width: 1.5rem;
                            }
                        }
                        #settings{
                            background: none;
                            border: none;
                            padding: 0rem 1rem 0rem 1rem;
                            min-height: 30px;
                            svg{
                                height: 1.5rem;
                                width: 1.5rem;
                            }
                        }
                    }

                    .profileStatsDiv{
                        margin: 0.7rem 0rem 0.7rem 0rem;
                        display: flex;
                        width: 330px;
                        justify-content: space-between;
                        align-items: center;
                    }
                    .subProfileDiv{
                        margin: 0rem 0rem 0.7rem 0rem;
                        .profileDescription{
                            max-width: 450px;
                        }
                    }
                    #followedBy{
                        color: darkgray;
                        font-size: 12px;
                        font-weight: 600;
                        b{
                            color: black;
                        }
                    }
                }
            }

            .mobileProfileDiv{
                display: none;
                flex-direction: column;
                justify-content: flex-start;
                align-items: center;
                min-height: 300px;
                max-height: fit-content;
                width: 100%;
                .marginDiv{
                    width: 90%;
                    
                    .mobileUpperDiv{
                        display: flex;
                        min-width: 320px;
                        width: 100%;
                        margin-top:1rem;
                        height: 150px;
                        align-items: center;
                        img{
                            width: 120px;
                            height: 100px;
                            border-radius: 50%;
                            object-fit: contain;
                            margin: 0rem 1rem 1rem 0rem;
                        }
                        .mobileUpperRightDiv{
                            display: flex;
                            flex-direction: column;
                            align-items: center;
                            justify-content: center;
                            height: 100%;
                            span{
                                h1{
                                    font-size: 32px;
                                    margin: 0%;
                                    font-weight: 300;
                                }
                                display: flex;
                                min-width: 280px;
                                margin-bottom: 1rem;
                            }
                            #settingButton{
                                width: 40px;
                                background:none;
                                border: none;
                                cursor: pointer;
                                height: 100%;
                            }
                            #dropDown{
                                background-color: $secColor;
                                color: white;
                                border: none;
                                padding: 0rem 1rem 0rem 1rem;
                                min-height: 30px;
                                border-radius: 5px;
                                svg{
                                    height: 1rem;
                                    width: 1rem;
                                }
                            }
                            #follow{
                                background-color: $secColor;
                                color: white;
                                border: none;
                                font-weight: bold;
                                padding: 0rem 1.5rem 0rem 1.5rem;
                                border-radius: 5px;
                                min-height: 30px;
                                min-width: 150px;
                                margin-right: 1rem;
                            }
                        }
                    }
                    .subProfileDiv{
                        width: 100%;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        
                        b{
                            font-weight: 600;
                        }
    
                        .profileDescription{
                            font-weight: 400;
                            max-width: 320px;                        
                            min-width: 260px;
                        }
                    }
                    #followedBy{
                        display: flex;
                        width: 100%;
                        color: darkgray;
                        font-size: 12px;
                        font-weight: 600;
                        b{
                            color: black;
                        }
                    } 
                }
                
                .profileStatsDiv{
                    min-width: 320px;
                    margin: 1rem 0rem 0rem 0rem;
                    display: flex;
                    width: 100%;
                    justify-content: space-around;
                    align-items: center;
                    border-top: 2px solid $line;
                    div{
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                        p{
                            font-weight: 600;
                        }
                        a{
                            text-decoration: none;
                            color: darkgray;
                            font-weight: 400;
                        }
                    }
                }
            }
            
        }

        .profileBottomDiv{
            width: 100%;
            border-top: 2px solid $line;
            display: flex;
            flex-direction: column;
            align-items: center;
            min-width: 320px;
            .postNavbar{
                display: flex;
                width: 100%;
                justify-content: center;
                align-items: center;
                button{

                    background:none;
                    border:none;
                    padding: 1rem 2rem 0rem 2rem;
                    font-size: 12px;
                    color: darkgray;
                    cursor: pointer;
                }
            }
            .postDiv{
                height: fit-content;
                width: 100%;
                display: flex;
                flex-wrap: wrap;
                flex-direction: row;
                justify-content: flex-start;
                background: $lightSecColor;
                border: none;
                button{
                    cursor: pointer;
                    margin: 1.5%;
                    background: none;
                    border: none;
                    position: relative;
                    width: 30%;
                }
                button:after{
                    content: "";
                    display: block;
                    padding-bottom: 100%;
                }
                img{
                    z-index: 1;
                    position: absolute;
                    top: 0%;
                    right: 0;
                    bottom: 0%;
                    left: 0%;
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    object-position: center;
                }
            }
        }


    }
    @media screen and (max-width: $tablet) {
        .homeInnerDiv{
            width: 100%;
            margin: 0rem 2rem 0rem 2rem;
            
            .profileUpperDiv{
                .desktopProfileDiv{
                    display: none;
                }
                .mobileProfileDiv{
                    display: flex;
                }
            }
           
            
        }

    }
    @media screen and (max-width: $phone) {
        .homeInnerDiv{
            width: 100%;
            margin: 0%;
            min-height: 500px;
            .profileUpperDiv{
                .mobileProfileDiv{
                    .marginDiv{
                        margin-left: 2rem;
                        margin-right: 2rem;
                        .mobileUpperDiv{
                            height: fit-content;
                            img{
                                width: 80px;
                                height: 80px;
                            }
                            .mobileUpperRightDiv{
                                span{
                                    min-width: 220px;
                                }
                                h1{
                                    font-size: 24px;
                                }
                                #follow{
                                    min-width: 120px;
                                }
                            }
                        }
                    
                    }
                }
                .subProfileDiv{
                    .profileDescription{
                        font-size: 14px;
                    }
                }
                .desktopProfileDiv{
                    display: none;
                }
                .mobileProfileDiv{
                    display: flex;
                }
            }
            .profileBottomDiv{
                .postDiv{
                    button{
                        margin: 0.3%;
                        width: 32%;
                    }
                }
            }
            
        }

    }
}

//loading
.loading{
    display:flex;
    flex-direction: column;
    min-height: 100vh;
    font-size: large;
    font-weight: bold;
    justify-content: center;
    align-items: center;
    background-color: $lightSecColor;
    a{
        text-decoration: underline;
        color: darkgray;
    }
}
// Upload

#uploadOuterDiv{
    display: flex;
    width: 100%;
    min-height: 100vh;
    align-items: center;
    justify-content: center;
    background-color: $lightSecColor;
    #uploadMainDiv{
        display: flex;
        justify-content: space-around;
        align-items: center;
        background-color: $lightSecColor;
        width: 100%;
        height: 500px;
        
        #uploadFormDiv{
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            height: fit-content;
            width: 480px;
            background-color: $lightSecColor;
            #uploadForm{
                padding: 2rem 4rem 4rem 4rem;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: space-between;
                background-color: $lightMainColor;
                border: 1px solid $line;
                height: 600px;
                h1{
                    margin: 0%;   
                    padding: 0%;
                    font-weight: 400;
                    width: 100%;
                    display: flex;
                }
                #uploadArea{
                    width: 100%;
                    height: 350px;
                    border: 2px solid black;
                    border-radius: 10px;
                    position: relative;
                    svg{
                        position: absolute;
                    }
                    #upload{
                        width: 100%;
                        height: 100%;
                        position: absolute;
                        opacity: 0;
                        cursor: pointer;
                    }
                }
                #textAreaDiv{
                    width: 100%;
                    font-weight: bold;
                    #caption{
                        width: 100%;
                        height: 150px;
                        border-radius: 10px;
                        resize: none;
                        font-family: Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
                        font-weight: 500;
                        font-size: 18px;
                    }
                }
                #buttonDiv{
                    width: 100%;

                    #uploadButton{
                        margin-top:1rem;
                        overflow: hidden;
                        text-decoration: none;
                        color: white;
                        background-color: $secColor;
                        padding: 0.4rem 1rem 0.4rem 1rem;
                        border-radius: 3px;
                        transition-duration: 0.6s;
                        
                        font-size: 0.8rem;
                        font-weight: 600;
                        width: 100%;
                        &:hover{
                            cursor: pointer;
                        }
                    }
                }
                
                
            }
            

        }
        
    }
    @media screen and (max-width: $phone) {
        #uploadMainDiv{
        display: flex;
        justify-content: space-around;
        align-items: center;
        background-color: $lightSecColor;
        width: 100%;
        height: 500px;
        
        #uploadFormDiv{
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            height: fit-content;
            width: 480px;
            background-color: $lightSecColor;
            #uploadForm{
                
                padding: 2rem 2rem 2rem 2rem;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: space-between;
                background-color: $lightMainColor;
                border: 1px solid $line;
                height: 600px;
                h1{
                    margin: 0%;   
                    padding: 0%;
                    font-weight: 400;
                    width: 100%;
                    display: flex;
                }
                #uploadArea{
                    width: 100%;
                    height: 300px;
                    border: 2px solid black;
                    border-radius: 10px;
                    #upload{
                        width: 100%;
                        height: 200px;
                        padding-top: 7rem;
                    }
                }
                #textAreaDiv{
                    width: 100%;
                    font-weight: bold;
                    #caption{
                        width: 100%;
                        border-radius: 10px;
                        resize: none;
                    }
                }
                #buttonDiv{
                    width: 100%;

                    #uploadButton{
                        margin-top:1rem;
                        overflow: hidden;
                        text-decoration: none;
                        color: white;
                        background-color: $secColor;
                        padding: 1rem 1rem 1rem 1rem;
                        border-radius: 8px;
                        transition-duration: 0.6s;
                        
                        font-size: 0.8rem;
                        font-weight: 600;
                        width: 100%;
                        &:hover{
                            cursor: pointer;
                        }
                    }
                }
                
                
            }
            

        }
        
    }

    }
    
}

// explore

.exploreOuterDiv{

    width: 100%;
    min-height: 100vh;
    padding-top: 60px;
    display: flex;
    justify-content: center;
    .exploreInnerDiv{
        width: 70%;
        
        .videoGlobalStrip{
            display: flex;
            height: 650px;
            
            justify-content: center;
            align-items: center;
            margin-top: 2rem;
            .videoLeft{
                display: flex;
                flex-direction: column;
                width: 34%;
                height: 600px;
                justify-content: space-around;
                img{
                    width: 90%;
                    height: 300px;
                    object-fit: cover;
                    padding: 2rem 2rem 0rem 0rem;
                }
            }
            .videoRight{
                width: 66%;
                display: flex;
                height: 600px;
                justify-content: center;
                align-items: center;
                img{
                    height: 570px;
                    width: 100%;
                    object-fit: cover;
                    padding: 2rem 0rem 0rem 0rem;
                }
            }
            
        }
        .imageStrip{
            display: flex;
            height: 650px;
            width: 100%;
            flex-wrap: wrap;
            justify-content: space-between;
            img{
                width: 31%;
                height: 300px;
                object-fit: cover;
                padding: 2rem 0rem 0rem 0rem;
            }
            
        }
    }
    @media screen and (max-width: $tablet) {
        .exploreInnerDiv{
            width: 90%;
            
            .videoGlobalStrip{
                margin-top: 0rem;
            }
        }

    }
    @media screen and (max-width: $phone) {
        .exploreInnerDiv{
            width: 100%;
            
            .videoGlobalStrip{
                display: flex;
                height: 350px;
                
                justify-content: center;
                align-items: center;
                margin-top: 0rem;
                .videoLeft{
                    display: flex;
                    flex-direction: column;
                    width: 34%;
                    height: 350px;
                    justify-content: space-around;
                    img{
                        width: 100%;
                        height: 350px;
                        object-fit: cover;
                        padding: 0%;
                    }
                }
                .videoRight{
                    width: 66%;
                    display: flex;
                    height: 350px;
                    justify-content: center;
                    align-items: center;
                    img{
                        height: 350px;
                        width: 100%;
                        object-fit: cover;
                        padding: 0%;
                    }
                }
                
            }
            .imageStrip{
                display: flex;
                height: 350px;
                width: 100%;
                flex-wrap: wrap;
                justify-content: space-between;
                img{
                    width: 33%;
                    height: 50%;
                    object-fit: cover;
                    padding: 0%;
                }
                
            }
        }

    }
}
